import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { supabase } from "./supabaseClient";
import "./index.css";

export default function BarBoxButton() {
  const navigate = useNavigate();
  const [name, setName] = useState("");
  const [key, setKey] = useState("");
  const [error, setError] = useState("");
  const [activeBox, setActiveBox] = useState(null);

  const handleLogin = async (type) => {
    if (!name || !key) {
      setError(" Please enter both Name and Key.");
      return;
    }

    let tableName = "";
    if (type === "federation") tableName = "members";
    if (type === "league") tableName = "league_members";
    if (type === "club") tableName = "club_members";

    const { data, error: supaError } = await supabase
      .from(tableName)
      .select("*")
      .eq("last_name", name)
      .eq("password", key)
      .single();

    if (supaError || !data) {
      setError(" Invalid Name or Key.");
      return;
    }

    setError("");

    if (type === "federation") navigate("/FederationPage", { state: { ...data } });
    if (type === "league") navigate("/LeaguePage", { state: { ...data } });
    if (type === "club") navigate("/AmateurSportsClubPage", { state: { ...data } });
  };

  const handleBackHome = () => {
    setActiveBox(null);
    setName("");
    setKey("");
    setError("");
  };

  const toggleBox = (box) => setActiveBox(activeBox === box ? null : box);

  const renderBox = (type, label, btnClass, addPath) => (
    <div className="w-full max-w-md text-center">
      <button className={`${btnClass} w-full`} onClick={() => toggleBox(type)}>
        {label}
      </button>
      {activeBox === type && (
        <div className="box p-6 mt-4 shadow-lg rounded-lg bg-white">
          <input
            type="text"
            placeholder="Enter Name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            className="input-field mb-2"
          />
          <input
            type="password"
            placeholder="Enter Key"
            value={key}
