import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import { supabase } from "./supabaseClient";
import Navigation from "./Navigation";
import BackHomeButton from "./BackHomeButton";
import { Shield } from "lucide-react";
import logo from "./assets/logo.png";

export default function LeaguePage() {
  const { state } = useLocation(); // user data from BarBoxButton
  const [leagueLogoUrl, setLeagueLogoUrl] = useState(null);
  const [leagueName, setLeagueName] = useState("");

  useEffect(() => {
    if (state && state.league_id) {
      fetchLeagueData(state.league_id, state.logo_url);
    }
  }, [state]);

  const fetchLeagueData = async (leagueId, logoPath) => {
    try {
      // 1 Get league name from nameleague table
      const { data: leagueData, error: leagueError } = await supabase
        .from("nameleague")
        .select("name_league")
        .eq("id", leagueId)
        .single();

      if (leagueError) console.error("Error fetching league name:", leagueError);
      else setLeagueName(leagueData?.name_league || "League Name");

      // 2 Get public URL for the logo in the bucket
      if (logoPath) {
        const { data: urlData, error: urlError } = supabase.storage
          .from("league-logos")
          .getPublicUrl(logoPath);

        if (urlError) console.error("Error getting logo URL:", urlError);
        else setLeagueLogoUrl(urlData?.publicUrl || null);
      }
    } catch (err) {
      console.error("Error fetching league data:", err);
    }
  };

  if (!state) return <p>No account data found.</p>;

  return (
    <div className="page min-h-screen flex flex-col">
      {/* HEADER with League Logo + Name */}
      <header className="p-4 flex items-center gap-4 bg-gray-100 shadow">
        {leagueLogoUrl ? (
          <img
            src={leagueLogoUrl}
            alt="League Logo"
            className="h-16 w-16 object-contain"
          />
        ) : (
          <Shield className="w-16 h-16 text-green-700" />
        )}
        <h1 className="text-2xl font-bold">{leagueName || "League Name"}</h1>
      </header>

      {/* MAIN CONTENT */}
      <section className="content p-6 flex-1">
        <h2 className="text-xl font-semibold mb-2">
          Welcome {state.first_name} {state.last_name}
        </h2>
        <p className="mb-4">
          <strong>Role:</strong> {state.role}
        </p>
        <BackHomeButton />
      </section>

      {/* NAVIGATION */}
      <Navigation />

      {/* FOOTER */}
      <footer
        className="footer p-4 text-white text-center"
        style={{ backgroundColor: "red" }}
      >
        <p>&copy; 2025 Algerian Judo Federation. All rights reserved.</p>
      </footer>
    </div>
  );
}

