import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import Navigation from "./Navigation";
import BackHomeButton from "./BackHomeButton";
import { Shield } from "lucide-react";
import { supabase } from "./supabaseClient";
import logo from "./assets/logo.png";

export default function LeaguePage() {
  const { state } = useLocation(); // state contains league_member data
  const [leagueName, setLeagueName] = useState("");
  const [leagueLogoUrl, setLeagueLogoUrl] = useState("");

  useEffect(() => {
    if (!state) return;

    const fetchLeagueInfo = async () => {
      try {
        // 1 Get league name from nameleague table
        const { data: leagueData, error: leagueError } = await supabase
          .from("nameleague")
          .select("name_league")
          .eq("id", state.league_id)
          .single();

        if (!leagueError && leagueData) {
          setLeagueName(leagueData.name_league);
        }

        // 2 Get league logo public URL
        if (state.logo_url) {
          const { data: urlData, error: urlError } = supabase.storage
            .from("league-logos")
            .getPublicUrl(state.logo_url); // full path: league-logos/league-logos/file.jpg

          if (!urlError && urlData?.publicUrl) {
            setLeagueLogoUrl(urlData.publicUrl);
          }
        }
      } catch (err) {
        console.error("Error fetching league info:", err);
      }
    };

    fetchLeagueInfo();
  }, [state]);

  if (!state) return <p>No account data found.</p>;

  return (
    <div className="page min-h-screen flex flex-col">
      {/* HEADER with League Logo + Name */}
      <header className="p-4 flex items-center gap-4 bg-gray-100 shadow">
        {leagueLogoUrl ? (
          <img
            src={leagueLogoUrl}
            alt="League Logo"
            className="h-16 w-16 object-contain"
          />
        ) : (
          <Shield className="w-16 h-16 text-green-700" />
        )}
        <h1 className="text-2xl font-bold">{leagueName || "League Name"}</h1>
      </header>

      {/* MAIN CONTENT */}
      <section className="content p-6 flex-1">
        <h2 className="text-xl font-semibold mb-2">
          Welcome {state.first_name} {state.last_name}
        </h2>
        <p className="mb-4">
          <strong>Role:</strong> {state.role}
        </p>
        <BackHomeButton />
      </section>

      {/* NAVIGATION */}
      <Navigation />

      {/* FOOTER */}
      <footer
        className="footer p-4 text-white text-center"
        style={{ backgroundColor: "red" }}
      >
        <p>&copy; 2025 Algerian Judo Federation. All rights reserved.</p>
      </footer>
    </div>
  );
}

</boltAction>


<boltAction type="file" filePath="/home/project/src/BarBoxButton.jsx">
import { useState } from "react";
import { useNavigate } from "react-router-dom";
import { supabase } from "./supabaseClient";

export default function BarBoxButton() {
  const [activeBox, setActiveBox] = useState(null);
  const [name, setName] = useState("");
  const [key, setKey] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();

  //  This is only used for Federation page
  const federationLogo = "https://example.com/federation-logo.png";
  const federationName = "Algerian Judo Federation";

  // Login handler depending on type
  const handleSubmit = async (target, type) => {
    if (!name || !key) {
      setError(" Please enter both Name and Key before continuing.");
      return;
    }

    let tableName = "";
    let nameColumn = "";
    let keyColumn = "";
    if (type === "federation") {
      tableName = "members";
      nameColumn = "last_name"; 
      keyColumn = "password";
    } else if (type === "league") {
      tableName = "league_members";
      nameColumn = "last_name";
      keyColumn = "password";
    } else if (type === "club") {
      tableName = "club_members";
      nameColumn = "last_name";
      keyColumn = "password";
    }

    const { data, error: supaError } = await supabase
      .from(tableName)
      .select("*")
      .eq(nameColumn, name)
      .eq(keyColumn, key)
      .single();

    if (supaError || !data) {
      setError(" Invalid Name or Key. Please try again.");
      return;
    }

    setError("");

    // Pass extra info for Federation, League, Club pages
    let extraData = {};
    if (type === "federation") extraData = { federationLogo, federationName };
    if (type === "league") extraData = { league_id: data.league_id, logo_url: data.logo_url };
    if (type === "club") extraData = { club_id: data.club_id, logo_url: data.logo_url };

    navigate(target, { state: { ...data, ...extraData } });
  };

  const handleAddMember = (target) => {
    navigate(target);
  };

  const resetBox = () => {
    setActiveBox(null);
    setName("");
    setKey("");
    setError("");
  };

  const renderBox = (type, label, addPath, btnClass = "secondary-btn") => (
    <div className="login-section">
      <button className={btnClass} onClick={() => setActiveBox(activeBox === type ? null : type)}>
        {label}
      </button>
      {activeBox === type && (
        <div className="login-box">
          <input
            type="text"
            placeholder="Enter Name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            className="input-field mb-2"
          />
          <input
            type="password"
            placeholder="Enter Key"
            value={key}
            onChange={(e) => setKey(e.target.value)}
            className="input-field mb-2"
          />
          {error && <p className="error-text">{error}</p>}
          <div className="btn-row mt-2 flex gap-2">
            <button className="primary-btn" onClick={() => handleSubmit(`/${type === "federation" ? "FederationPage" : type === "league" ? "LeaguePage" : "AmateurSportsClubPage"}`, type)}>
              Login
            </button>
            <button className="secondary-btn" onClick={() => handleAddMember(addPath)}>
               Add Member
            </button>
            <button className="secondary-btn" onClick={resetBox}>
              Back Home
            </button>
          </div>
        </div>
      )}
    </div>
  );

  return (
    <div className="center-block flex flex-col gap-4">
      {renderBox("federation", "Federation Account", "/AddMemberPage", "primary-btn")}
      {renderBox("league", "League Account", "/AddMemberLeague")}
      {renderBox("club", "Amateur Sports Club", "/AddMemberClub")}
    </div>
  );
}

</boltAction>


