import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { Shield } from "lucide-react";
import logo from "./assets/logo.png";
import { supabase } from "./supabaseClient";
import Navigation from "./Navigation";
import BackHomeButton from "./BackHomeButton";

export default function AddMemberLeague() {
  // form fields
  const [role, setRole] = useState("");
  const [bloodType, setBloodType] = useState("");
  const [lastName, setLastName] = useState("");
  const [firstName, setFirstName] = useState("");
  const [dob, setDob] = useState("");
  const [pob, setPob] = useState("");
  const [nid, setNid] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");

  // league dropdown
  const [leagues, setLeagues] = useState([]);
  const [selectedLeagueId, setSelectedLeagueId] = useState("");

  // logo upload
  const [logoFile, setLogoFile] = useState(null);
  const [logoUrl, setLogoUrl] = useState(null);

  // UI state
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  // header
  const [federationLogo, setFederationLogo] = useState(null);
  const [federationName, setFederationName] = useState("Algerian Judo Federation");

  const navigate = useNavigate();

  // roles
  const [roleId, setRoleId] = useState("");
  const [roles, setRoles] = useState([]);

  // fetch federation logo
  useEffect(() => {
    (async () => {
      const { data, error } = await supabase
        .from("logo")
        .select("logo_url")
        .order("created_at", { ascending: false })
        .limit(1);

      if (!error && data && data.length > 0) setFederationLogo(data[0].logo_url);
    })();
  }, []);

  // fetch leagues
  useEffect(() => {
    const fetchLeagues = async () => {
      const { data, error } = await supabase
        .from("nameleague")
        .select("id, name_league")
        .order("name_league", { ascending: true });

      if (error) setError(` Failed to load leagues: ${error.message}`);
      else setLeagues(data || []);
    };
    fetchLeagues();
  }, []);

  // fetch roles
  useEffect(() => {
    const fetchRoles = async () => {
      const { data, error } = await supabase.from("leaguerole").select("id, league_role");
      if (!error) setRoles(data);
    };
    fetchRoles();
  }, []);

  // upload logo directly to bucket root
  const handleLogoUpload = async () => {
    if (!logoFile) return null;

    const fileExt = logoFile.name.split(".").pop();
    const fileName = `${Date.now()}.${fileExt}`; // direct root
    const { error: uploadError } = await supabase.storage
      .from("league-logos")
      .upload(fileName, logoFile);

    if (uploadError) {
      setError(` Failed to upload logo: ${uploadError.message}`);
      return null;
    }

    const { data } = supabase.storage.from("league-logos").getPublicUrl(fileName);
    return data?.publicUrl ?? null;
  };

  // submit
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!selectedLeagueId) {
      setError(" Please choose a League Name.");
      return;
    }
    if (!roleId) {
      setError(" Please choose a League Role.");
      return;
    }

    if (
      !bloodType ||
      !lastName ||
      !firstName ||
      !dob ||
      !pob ||
      !nid ||
      !password ||
      !confirmPassword
    ) {
      setError(" All fields are required.");
      return;
    }

    if (nid.length !== 18) {
      setError(" National Identity Number must be exactly 18 digits.");
      return;
    }

    if (password !== confirmPassword) {
      setError(" Passwords do not match.");
      return;
    }

    setError("");

    let uploadedLogoUrl = null;
    if (logoFile) {
      uploadedLogoUrl = await handleLogoUpload();
      if (!uploadedLogoUrl) return;
      setLogoUrl(uploadedLogoUrl);
    }

    const newMember = {
      last_name: lastName,
      first_name: firstName,
      date_of_birth: dob,
      place_of_birth: pob,
      role: roles.find(r => r.id === parseInt(roleId))?.league_role ?? "",
      blood_type: bloodType,
      national_id_number: nid,
      password,
      license_number: "LIC-" + Date.now(),
      registration_date: new Date().toISOString().split("T")[0],
      logo_url: uploadedLogoUrl,
      league_id: selectedLeagueId,
    };

    const { error: insertError } = await supabase.from("league_members").insert([newMember]);

    if (insertError) {
      setError(` Failed to add member: ${insertError.message}`);
      return;
    }

    setSuccess(` League Member "${firstName} ${lastName}" added successfully!`);

    // reset
    setLastName("");
    setFirstName("");
    setDob("");
    setPob("");
    setNid("");
    setPassword("");
    setConfirmPassword("");
    setRole("");
    setBloodType("");
    setSelectedLeagueId("");
    setLogoFile(null);
    setLogoUrl(null);
  };

  return (
    <div className="page-container">
      <div className="content-box">
        {/* Header */}
        <header className="bg-white text-gray-800 p-6 shadow-lg border-b-4 border-red-500">
          <div className="container mx-auto">
            <div className="federation-header">
              {federationLogo ? (
                <img src={federationLogo} alt="Logo Fdration" className="federation-logo" />
              ) : (
                <Shield className="w-16 h-16 text-green-700" />
              )}
              <h1 className="federation-title">{federationName}</h1>
            </div>
          </div>
        </header>

        <h2 className="form-title">Add a New League Member</h2>

        <form onSubmit={handleSubmit} className="form-grid">
          {/* League Name */}
          <label>
            League Name
            <select
              value={selectedLeagueId}
              onChange={(e) => setSelectedLeagueId(e.target.value)}
              required
