    if (!roleId) {
      setError(" Please choose a League Role.");
      return;
    }

    if (
      !bloodType ||
      !lastName ||
      !firstName ||
      !dob ||
      !pob ||
      !nid ||
      !password ||
      !confirmPassword
    ) {
      setError(" All fields are required.");
      return;
    }

    if (nid.length !== 18) {
      setError(" National Identity Number must be exactly 18 digits.");
      return;
    }

    if (password !== confirmPassword) {
      setError(" Passwords do not match.");
      return;
    }

    setError("");

    let uploadedLogoUrl = null;
    if (logoFile) {
      uploadedLogoUrl = await handleLogoUpload();
      if (!uploadedLogoUrl) return;
      setLogoUrl(uploadedLogoUrl);
    }

    const newMember = {
      last_name: lastName,
      first_name: firstName,
      date_of_birth: dob,
      place_of_birth: pob,
      role: roles.find(r => r.id === parseInt(roleId))?.league_role ?? "",
      blood_type: bloodType,
      national_id_number: nid,
      password,
      license_number: "LIC-" + Date.now(),
      registration_date: new Date().toISOString().split("T")[0],
      logo_url: uploadedLogoUrl,
      league_id: selectedLeagueId,
