    const { error: insertError } = await supabase.from("league_members").insert([newMember]);

    if (insertError) {
      setError(` Failed to add member: ${insertError.message}`);
      return;
    }

    setSuccess(` League Member "${firstName} ${lastName}" added successfully!`);

    // reset
    setLastName("");
    setFirstName("");
    setDob("");
    setPob("");
    setNid("");
    setPassword("");
    setConfirmPassword("");
    setRole("");
    setBloodType("");
    setSelectedLeagueId("");
    setLogoFile(null);
    setLogoUrl(null);
  };

  return (
    <div className="page-container">
      <div className="content-box">
        {/* Header */}
        <header className="bg-white text-gray-800 p-6 shadow-lg border-b-4 border-red-500">
          <div className="container mx-auto">
            <div className="federation-header">
              {federationLogo ? (
                <img src={federationLogo} alt="Logo Fdration" className="federation-logo" />
              ) : (
                <Shield className="w-16 h-16 text-green-700" />
              )}
              <h1 className="federation-title">{federationName}</h1>
            </div>
          </div>
        </header>

        <h2 className="form-title">Add a New League Member</h2>

        <form onSubmit={handleSubmit} className="form-grid">
          {/* League Name */}
          <label>
            League Name
            <select
              value={selectedLeagueId}
              onChange={(e) => setSelectedLeagueId(e.target.value)}
              required
            >
              <option value="">-- Select a League --</option>
              {leagues.map((l) => (
                <option key={l.id} value={l.id}>
                  {l.name_league}
                </option>
              ))}
            </select>
          </label>

          {/* Role */}
          <label>
            League Role
            <select value={roleId} onChange={(e) => setRoleId(e.target.value)} required>
              <option value="">-- Select Role --</option>
              {roles.map((role) => (
                <option key={role.id} value={role.id}>
                  {role.league_role}
                </option>
              ))}
            </select>
          </label>

          {/* Blood Type */}
          <label>
            Blood Type
            <select value={bloodType} onChange={(e) => setBloodType(e.target.value)}>
              <option value="">-- Select Blood Type --</option>
              <option>A+</option>
              <option>A-</option>
              <option>B+</option>
              <option>B-</option>
              <option>AB+</option>
              <option>AB-</option>
              <option>O+</option>
              <option>O-</option>
            </select>
          </label>

          {/* Text Inputs */}
          <label>
            Last Name
            <input type="text" value={lastName} onChange={(e) => setLastName(e.target.value)} />
          </label>

          <label>
            First Name
            <input type="text" value={firstName} onChange={(e) => setFirstName(e.target.value)} />
          </label>

          <label>
            Date of Birth
            <input type="date" value={dob} onChange={(e) => setDob(e.target.value)} />
          </label>

          <label>
            Place of Birth
            <input type="text" value={pob} onChange={(e) => setPob(e.target.value)} />
          </label>

          <label>
            National Identity Number
            <input type="text" value={nid} onChange={(e) => setNid(e.target.value)} maxLength={18} />
          </label>

          {/* Passwords */}
          <label>
            Password
