              <label>First Name<input type="text" value={firstName} onChange={e => setFirstName(e.target.value)} required /></label>
              <label>Date of Birth<input type="date" value={dob} onChange={e => setDob(e.target.value)} required /></label>
              <label>Place of Birth<input type="text" value={pob} onChange={e => setPob(e.target.value)} required /></label>
