              <label>Email<input type="email" value={email} onChange={e => setEmail(e.target.value)} required /></label>
              <label>Password<input type="password" value={password} onChange={e => setPassword(e.target.value)} required /></label>
              <label>Confirm Password<input type="password" value={confirmPassword} onChange={e => setConfirmPassword(e.target.value)} required /></label>

              <label>
                Upload League Logo
                <input type="file" accept="image/*" onChange={e => setLogoFile(e.target.files?.[0] ?? null)} />
              </label>

              <div className="btn-row">
                <button type="submit" className="primary-btn">Add Member</button>
                <button type="button" className="secondary-btn" onClick={() => navigate("/league-member-list")}>
                  View Member List
                </button>
                <BackHomeButton />
              </div>
            </form>

            {error && <p className="error-text">{error}</p>}
            {success && <p className="success-text">{success}</p>}

            <Navigation />
          </div>
        </div>
      );
    }
  </boltAction>
