  <boltAction type="supabase" operation="migration" filePath="supabase/migrations/remove_password_from_league_members.sql">
    /*
      # Remove password column from league_members

      1.  **Changes**
          - Removes the `password` column from the `league_members` table. Passwords should be managed exclusively by Supabase's `auth.users` table.

      2.  **Reasoning**
          - Storing plain-text or even hashed passwords in a public table is a major security vulnerability.
          - This change aligns the application with security best practices by relying on Supabase's built-in authentication.
    */

    DO $$
    BEGIN
      IF EXISTS (
        SELECT 1 FROM information_schema.columns
        WHERE table_schema = 'public' AND table_name = 'league_members' AND column_name = 'password'
      ) THEN
        ALTER TABLE public.league_members DROP COLUMN password;
      END IF;
