import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { Shield } from "lucide-react";
import { supabase } from "./supabaseClient";
import Navigation from "./Navigation";
import BackHomeButton from "./BackHomeButton";

export default function AddMemberLeague() {
  const [lastName, setLastName] = useState("");
  const [firstName, setFirstName] = useState("");
  const [dob, setDob] = useState("");
  const [pob, setPob] = useState("");
  const [roleId, setRoleId] = useState("");
  const [roles, setRoles] = useState([]);
  const [bloodType, setBloodType] = useState("");
  const [nid, setNid] = useState("");
  const [password, setPassword] = useState("");
  const [selectedLeagueId, setSelectedLeagueId] = useState("");
  const [leagues, setLeagues] = useState([]);
  const [logoFile, setLogoFile] = useState(null);
  const [error, setError] = useState("");

  const navigate = useNavigate();

  useEffect(() => {
    const fetchRoles = async () => {
      const { data, error } = await supabase
        .from("leaguerole")
        .select("id, league_role");
      if (!error && data) setRoles(data);
    };
    fetchRoles();
  }, []);

  useEffect(() => {
    const fetchLeagues = async () => {
      const { data, error } = await supabase
        .from("nameleague")
        .select("id, name_league");
      if (!error && data) setLeagues(data);
    };
    fetchLeagues();
  }, []);

  const handleLogoUpload = async () => {
    if (!logoFile) return null;

    const fileExt = logoFile.name.split(".").pop();
    const fileName = `${Date.now()}.${fileExt}`;

    // upload file
    const { error: uploadError } = await supabase.storage
      .from("logos")
      .upload(fileName, logoFile);

    if (uploadError) {
      setError(`Error uploading logo: ${uploadError.message}`);
      return null;
    }

    // always return the public URL
    return `https://aolsbxfulbvpiobqqsao.supabase.co/storage/v1/object/public/logos/${fileName}`;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");

    try {
      const uploadedLogo = await handleLogoUpload();

      const newMember = {
        last_name: lastName,
        first_name: firstName,
        date_of_birth: dob,
        place_of_birth: pob,
        role: roles.find((r) => r.id === parseInt(roleId))?.league_role || "",
        blood_type: bloodType,
        national_id_number: nid,
        password: password,
        license_number: "LIC-" + Date.now(),
        registration_date: new Date().toISOString().split("T")[0],
        logo_url: uploadedLogo, //  never NULL anymore
        league_id: selectedLeagueId,
      };

      const { error: insertError } = await supabase
        .from("league_members")
        .insert([newMember]);

      if (insertError) {
        setError("Error saving member: " + insertError.message);
      } else {
        alert("League Member added successfully!");
        navigate("/");
      }
    } catch (err) {
      setError("Unexpected error: " + err.message);
    }
  };

  return (
    <div className="page">
      <header className="header">
        <Shield className="header-icon" />
        <h1>Add League Member</h1>
      </header>

      <section className="content">
        <form onSubmit={handleSubmit} className="form">
          <input
            type="text"
            placeholder="First Name"
            value={firstName}
            onChange={(e) => setFirstName(e.target.value)}
            required
          />
          <input
            type="text"
            placeholder="Last Name"
            value={lastName}
            onChange={(e) => setLastName(e.target.value)}
            required
          />
          <input
            type="date"
            value={dob}
            onChange={(e) => setDob(e.target.value)}
            required
          />
          <input
            type="text"
            placeholder="Place of Birth"
            value={pob}
            onChange={(e) => setPob(e.target.value)}
            required
          />

          <select
            value={roleId}
            onChange={(e) => setRoleId(e.target.value)}
            required
          >
            <option value="">Select Role</option>
            {roles.map((r) => (
              <option key={r.id} value={r.id}>
                {r.league_role}
              </option>
            ))}
          </select>

          <input
            type="text"
            placeholder="Blood Type"
            value={bloodType}
            onChange={(e) => setBloodType(e.target.value)}
          />
          <input
            type="text"
            placeholder="National ID"
            value={nid}
            onChange={(e) => setNid(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />

          <select
            value={selectedLeagueId}
            onChange={(e) => setSelectedLeagueId(e.target.value)}
            required
          >
            <option value="">Select League</option>
            {leagues.map((l) => (
              <option key={l.id} value={l.id}>
                {l.name_league}
              </option>
            ))}
          </select>

          <input type="file" onChange={(e) => setLogoFile(e.target.files[0])} />

          <button type="submit">Save Member</button>
        </form>

        {error && <p className="error">{error}</p>}
        <BackHomeButton />
      </section>

      <Navigation />

      <footer className="footer">
        <p>&copy; 2025 Algerian Judo Federation. All rights reserved.</p>
      </footer>
    </div>
  );
}

