import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import Navigation from "./Navigation";
import BackHomeButton from "./BackHomeButton";
import { supabase } from "./supabaseClient";
import logo from "./assets/logo.png"; // federation placeholder

export default function AmateurSportsClubPage() {
  const { state } = useLocation();

  const [federationLogo, setFederationLogo] = useState(null);
  const [federationName] = useState("Algerian Judo Federation");

  const [leagueName, setLeagueName] = useState("");
  const [leagueLogo, setLeagueLogo] = useState(null);

  const [clubName, setClubName] = useState("");
  const [clubLogo, setClubLogo] = useState(null);

  const [loggedInMember, setLoggedInMember] = useState(null);
  const [clubMembers, setClubMembers] = useState([]);

  // fetch federation logo
  useEffect(() => {
    const fetchFederationLogo = async () => {
      const { data, error } = await supabase
        .from("logo")
        .select("logo_url")
        .order("created_at", { ascending: false })
        .limit(1);

      if (!error && data.length > 0) setFederationLogo(data[0].logo_url);
    };
    fetchFederationLogo();
  }, []);

  // fetch league info + logo
  useEffect(() => {
    if (!state?.league_id) return;

    const fetchLeague = async () => {
      const { data: leagueData } = await supabase
        .from("nameleague")
        .select("name_league")
        .eq("id", state.league_id)
        .single();
      if (leagueData) setLeagueName(leagueData.name_league);

      // get logo_url from league_members
      const { data: leagueLogoData } = await supabase
        .from("league_members")
        .select("logo_url")
        .eq("league_id", state.league_id)
        .order("id", { ascending: false })
        .limit(1)
        .single();
      if (leagueLogoData?.logo_url) setLeagueLogo(leagueLogoData.logo_url);
    };

    fetchLeague();
  }, [state?.league_id]);

  // fetch club info + logo
  useEffect(() => {
    if (!state?.club_id) return;

    const fetchClub = async () => {
      const { data: clubData } = await supabase
        .from("nameclub")
        .select("name_club")
        .eq("id", state.club_id)
        .single();
      if (clubData) setClubName(clubData.name_club);

      // get club logo from club_members
      const { data: memberLogoData } = await supabase
        .from("club_members")
        .select("logo_url")
        .eq("club_id", state.club_id)
        .order("id", { ascending: false })
        .limit(1)
        .single();
      if (memberLogoData?.logo_url) setClubLogo(memberLogoData.logo_url);
    };

    fetchClub();
  }, [state?.club_id]);

  // fetch logged-in member (for first_name, last_name, club_role)
  useEffect(() => {
    if (!state?.id) return;

    const fetchMember = async () => {
      const { data: memberData } = await supabase
        .from("club_members")
        .select("first_name, last_name, club_role")
        .eq("id", state.id)
        .single();
      if (memberData) setLoggedInMember(memberData);
    };

    fetchMember();
  }, [state?.id]);

  // fetch all club members
  useEffect(() => {
    if (!state?.club_id) return;

    const fetchAllMembers = async () => {
      const { data: membersData } = await supabase
        .from("club_members")
        .select("first_name, last_name, club_role")
        .eq("club_id", state.club_id)
        .order("first_name", { ascending: true });
      if (membersData) setClubMembers(membersData);
    };

    fetchAllMembers();
  }, [state?.club_id]);

  if (!state) return <p>No account data found.</p>;
  return (
    <div className="app-container">
      {/* HEADER Fdration + League + Club */}
      <header>
        <div className="federation-header">

          {/* Federation row */}
          <div style={{ display: "flex", alignItems: "center", gap: "1rem" }}>
            {federationLogo ? (
              <img src={federationLogo} alt="Federation Logo" className="federation-logo" />
            ) : (
              <img src={federationLogoPlaceholder} alt="Default Federation Logo" className="federation-logo" />
            )}
            <h1 className="federation-title">
              {federationName || "Algerian Judo Federation"}
            </h1>
          </div>


					 {/* League */}
          <div className="flex items-center gap-4">
            {leagueLogo && (
              <img
                src={leagueLogo}
                alt="League Logo"
                className="member-logo"
              />
            )}
            <h2 className="federation-title text-xl">{leagueName}</h2>
          </div>


          {/* Club row */}
          <div style={{ display: "flex", alignItems: "center", gap: "1rem" }}>
            {clubLogo && (
              <img src={clubLogo} alt="Club Logo" className="member-logo" />
            )}
            <h2 className="federation-title" style={{ fontSize: "1.25rem" }}>
              {clubName || "Club Name"}
            </h2>
          </div>
        </div>
      </header>

      {/* MAIN CONTENT */}
      <section className="content">
        <h2>Club Members</h2>
        {members.length > 0 ? (
          <ul>
            {members.map((m, idx) => (
              <li key={idx}>
                {m.first_name} {m.last_name}  <strong>{m.club_role}</strong>
              </li>
            ))}
          </ul>
        ) : (
          <p>No members found for this club.</p>
        )}
        <BackHomeButton />
      </section>

      {/* NAVIGATION */}
      <Navigation />

      {/* FOOTER */}
      <footer className="footer" style={{ backgroundColor: "red" }}>
        <p>&copy; 2025 Algerian Judo Federation. All rights reserved.</p>
      </footer>
    </div>
  );
}

