    const fetchPubs = async () => {
      const { data, error } = await supabase.from("pub").select("*");
      if (!error) setPubs(data);
    };
    fetchPubs();
  }, []);

  useEffect(() => {
    const fetchPublicas = async () => {
      const { data, error } = await supabase.from("publica").select("*");
      if (!error) setPublicas(data);
    };
    fetchPublicas();
  }, []);

  return (
