              alert("Error uploading publica");
            } else {
              const { data: urlData } = supabase.storage
                .from("publications")
                .getPublicUrl(filePath);

              // Save in state
              setPublicas((prev) => [
                ...prev,
                {
                  title: publicaTitle,
                  description: publicaDescription,
                  url: urlData.publicUrl,
                },
              ]);

              // Reset fields
              setPublicaTitle("");
              setPublicaDescription("");
              setPublicaFile(null);

              alert("Publica uploaded successfully!");
            }
          }}
        >
          Save Publica
        </button>
      </div>
      {/* NAVIGATION */}
      <Navigation />

      {/* FOOTER */}
      <footer className="footer" style={{ backgroundColor: "red" }}>
        <p>&copy; 2025 Algerian Judo Federation. All rights reserved.</p>
      </footer>
    </div>
  );
}
