import { supabase } from "./supabaseClient";
import { useNavigate } from "react-router-dom";
import Navigation from "./Navigation";
import BackHomeButton from "./BackHomeButton";
import { Shield } from "lucide-react";
import logo from "./assets/logo.png";

export default function FederationPage() {
  const navigate = useNavigate();
  const [photos, setPhotos] = useState([]);
  const [file, setFile] = useState(null);
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [errorMessage, setErrorMessage] = useState("");
  const [logoFile, setLogoFile] = useState(null);
  const [uploading, setUploading] = useState(false);
  const [logoUrl, setLogoUrl] = useState(null);
  const [federationLogo, setFederationLogo] = useState(null);
  const [federationName] = useState("Algerian Judo Federation");

  //  Add pubFile and publicaFile to state
  const [pubFile, setPubFile] = useState(null);
  const [publicaFile, setPublicaFile] = useState(null);

  useEffect(() => {
    fetchPhotos();
    fetchLatestLogo();
  }, []);

  const fetchPhotos = async () => {
    const { data, error } = await supabase
      .from("publications")
      .select("*")
      .order("created_at", { ascending: false });

    if (error) console.error("Error fetching photos:", error);
    else setPhotos(data || []);
  };

  const fetchLatestLogo = async () => {
    const { data, error } = await supabase
      .from("logo")
      .select("logo_url")
      .order("created_at", { ascending: false })
      .limit(1);

    if (!error && data.length > 0) setFederationLogo(data[0].logo_url);
  };

  const handleSaveLogo = async () => {
    if (!logoFile) {
      alert("Please select a logo file first.");
      return;
    }

    try {
      setUploading(true);

      const fileExt = logoFile.name.split(".").pop();
      const fileName = `logo-${Date.now()}.${fileExt}`;

      // Upload logo to logos bucket
      const { error: uploadError } = await supabase.storage
        .from("logos")
        .upload(fileName, logoFile, { upsert: true });

      if (uploadError) throw uploadError;

      const { data: urlData, error: urlError } = supabase.storage
        .from("logos")
        .getPublicUrl(fileName);

      if (urlError || !urlData?.publicUrl) throw urlError;

      const publicUrl = urlData.publicUrl;

      // Insert into logo table - SIMPLIFIED INSERT STATEMENT
      const { error: dbError } = await supabase.from("logo").insert([
        { logo_url: publicUrl },
      ]);

      if (dbError) throw dbError;

      setLogoUrl(publicUrl);
      alert(" Logo uploaded and saved successfully!");
    } catch (err) {
      console.error(err);
      alert(" Failed to save logo: " + (err.message || err));
    } finally {
      setUploading(false);
    }
  };

  // Save publication (unchanged)
  const handleSave = async () => {
    if (!file) {
      setErrorMessage("Please choose a file first.");
      return;
    }

    const fileName = `${Date.now()}-${file.name}`;

    const { error: uploadError } = await supabase.storage
      .from("publications")
      .upload(fileName, file, { upsert: true });

    if (uploadError) {
      setErrorMessage("Failed to save photo: " + uploadError.message);
      return;
    }

    const { data: urlData } = supabase.storage
      .from("publications")
      .getPublicUrl(fileName);

    const { error: dbError } = await supabase.from("publications").insert([
      {
        title,
        description,
        photo_url: urlData.publicUrl,
      },
    ]);

    if (dbError) setErrorMessage("Failed to save to DB: " + dbError.message);
    else {
      setErrorMessage("");
      setFile(null);
      setTitle("");
      setDescription("");
      fetchPhotos();
    }
  };

  const handleLogoChange = (e) => {
    setLogoFile(e.target.files?.[0] || null);
  };

  //  Define onChange handlers for pubFile and publicaFile
  const handlePubFileChange = (e) => {
    setPubFile(e.target.files?.[0] || null);
  };

  const handlePublicaFileChange = (e) => {
    setPublicaFile(e.target.files?.[0] || null);
  };

  //  Add two new states
  const [pubs, setPubs] = useState([]);
  const [publicas, setPublicas] = useState([]);

  // Add state variables for publica title and description
  const [publicaTitle, setPublicaTitle] = useState("");
  const [publicaDescription, setPublicaDescription] = useState("");

  //  Add two new effects
  useEffect(() => {
    const fetchPubs = async () => {
      const { data, error } = await supabase.from("pub").select("*");
      if (!error) setPubs(data);
    };
    fetchPubs();
  }, []);

  useEffect(() => {
    const fetchPublicas = async () => {
      const { data, error } = await supabase.from("publica").select("*");
      if (!error) setPublicas(data);
    };
    fetchPublicas();
  }, []);

  return (
    <div className="app-container">
      {/* HEADER */}
      <header className="bg-white text-gray-800 p-6 shadow-lg border-b-4 border-red-500">
        <div className="container mx-auto">
          <div className="federation-header">
            {federationLogo ? (
              <img
                src={federationLogo}
                alt="Logo Fdration"
                className="federation-logo"
              />
            ) : (
              <Shield className="w-16 h-16 text-green-700" />
            )}
            <h1 className="federation-title">
              {federationName || "Algerian Judo Federation"}
            </h1>
          </div>
        </div>
      </header>

      {/* MAIN CONTENT */}
      <section className="content">
        <h2>Welcome to the Federation Account</h2>
        <p>This is the Federation Account page.</p>
        <BackHomeButton />

        {/* Upload logo */}
        <div>
          <h2>Update Federation Logo</h2>
          <input type="file" accept="image/*" onChange={handleLogoChange} />
          <button
            onClick={handleSaveLogo}
            disabled={uploading}
            className="ml-2 px-4 py-2 bg-green-600 text-white rounded"
          >
            {uploading ? "Uploading..." : "Save Logo"}
          </button>
        </div>

        {/* List publications */}
        {errorMessage && <p style={{ color: "red" }}>{errorMessage}</p>}
        <h2>Update Federation Publication</h2>
        <table className="member-table">
          <thead>
            <tr>
              <th>Photo</th>
              <th>Title</th>
              <th>Description</th>
              <th>Created At</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {photos.length > 0 ? (
              photos.map((p) => (
                <tr key={p.id}>
                  <td>
                    {p.photo_url ? (
                      <img
                        src={p.photo_url}
                        alt={p.title}
                        width="80"
                        height="80"
                      />
                    ) : (
                      "No photo"
                    )}
                  </td>
                  <td>{p.title}</td>
                  <td>{p.description}</td>
                  <td>{new Date(p.created_at).toLocaleString()}</td>
                  <td>
                    <button className="primary-btn" onClick={() => handleDelete(p.id)}>
                      Delete
                    </button>
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="5">No photos found.</td>
              </tr>
            )}
          </tbody>
        </table>

        <div className="form-grid">
          <input type="file" onChange={(e) => setFile(e.target.files[0])} />
          <input
            type="text"
            placeholder="Title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
          />
          <input
            type="text"
            placeholder="Description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
          />
          <button className="primary-btn" onClick={handleSave}>
            Save
          </button>
          <button className="secondary-btn" onClick={() => window.print()}>
            Print
          </button>
        </div>
      </section>

      {/* PUB UPLOAD */}
      <div className="upload-section">
        <h2>Upload Pub</h2>
        <input
          type="file"
          accept="image/*"
          onChange={handlePubFileChange} //  Use the defined handler
        />
        <button
          onClick={async () => {
            if (!pubFile) return alert("Please select a file first");

            const filePath = `pubs/${Date.now()}_${pubFile.name}`;
            const { data, error } = await supabase.storage
              .from("publications")
              .upload(filePath, pubFile);

            if (error) {
              console.error("Error uploading pub:", error);
              alert("Error uploading pub");
            } else {
              const { data: urlData } = supabase.storage
                .from("publications")
                .getPublicUrl(filePath);

              setPubs((prev) => [...prev, { url: urlData.publicUrl }]);
              alert("Pub uploaded successfully!");
            }
          }}
        >
          Save Pub
        </button>
      </div>

      {/* PUBLICA UPLOAD */}
      <div className="upload-section">
        <h2>Upload Publica</h2>

        {/* Title */}
        <input
          type="text"
          placeholder="Enter title"
          value={publicaTitle}
          onChange={(e) => setPublicaTitle(e.target.value)}
          style={{ display: "block", marginBottom: "0.5rem", width: "100%" }}
        />

        {/* Description */}
        <textarea
          placeholder="Enter description"
          value={publicaDescription}
          onChange={(e) => setPublicaDescription(e.target.value)}
          style={{ display: "block", marginBottom: "0.5rem", width: "100%" }}
        />

        {/* Image file */}
        <input
          type="file"
          accept="image/*"
          onChange={(e) => setPublicaFile(e.target.files[0])}
        />

        {/* Save Button */}
        <button
          onClick={async () => {
            if (!publicaFile || !publicaTitle || !publicaDescription) {
              return alert("Please fill in all fields and select an image");
            }

            const filePath = `publicas/${Date.now()}_${publicaFile.name}`;
            const { error } = await supabase.storage
              .from("publications")
              .upload(filePath, publicaFile);

            if (error) {
              console.error("Error uploading publica:", error);
              alert("Error uploading publica");
            } else {
              const { data: urlData } = supabase.storage
                .from("publications")
                .getPublicUrl(filePath);

              // Save in state
              setPublicas((prev) => [
                ...prev,
                {
                  title: publicaTitle,
                  description: publicaDescription,
                  url: urlData.publicUrl,
                },
              ]);

              // Reset fields
              setPublicaTitle("");
              setPublicaDescription("");
              setPublicaFile(null);

              alert("Publica uploaded successfully!");
            }
          }}
        >
          Save Publica
        </button>
      </div>
      {/* NAVIGATION */}
      <Navigation />

      {/* FOOTER */}
      <footer className="footer" style={{ backgroundColor: "red" }}>
        <p>&copy; 2025 Algerian Judo Federation. All rights reserved.</p>
      </footer>
    </div>
  );
}
</boltAction>
  <boltAction type="supabase" operation="migration" filePath="/supabase/migrations/add_rls_policies_publications_bucket.sql">/*
  # Add RLS policies for publications bucket

  1. Security
    - Add policy to allow authenticated users to upload to the publications bucket
    - Add policy to allow anyone to read from the publications bucket
*/

-- Allow anyone to upload
create policy "Allow uploads" on storage.objects
for insert to authenticated
with check (bucket_id = 'publications');

-- Allow anyone to read
create policy "Allow read" on storage.objects
for select using (bucket_id = 'publications');
</boltAction>
  <boltAction type="supabase" operation="query" projectId="${projectId}">/*
  # Add RLS policies for publications bucket

  1. Security
    - Add policy to allow authenticated users to upload to the publications bucket
    - Add policy to allow anyone to read from the publications bucket
*/

-- Allow anyone to upload
create policy "Allow uploads" on storage.objects
for insert to authenticated
with check (bucket_id = 'publications');

-- Allow anyone to read
create policy "Allow read" on storage.objects
for select using (bucket_id = 'publications');
</boltAction>
