        {errorMessage && <p style={{ color: "red" }}>{errorMessage}</p>}
        <h2>Update Federation Publication</h2>
        <table className="member-table">
          <thead>
            <tr>
              <th>Photo</th>
              <th>Title</th>
              <th>Description</th>
              <th>Created At</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {photos.length > 0 ? (
              photos.map((p) => (
                <tr key={p.id}>
                  <td>
                    {p.photo_url ? (
                      <img
                        src={p.photo_url}
                        alt={p.title}
                        width="80"
                        height="80"
                      />
                    ) : (
                      "No photo"
                    )}
                  </td>
                  <td>{p.title}</td>
                  <td>{p.description}</td>
                  <td>{new Date(p.created_at).toLocaleString()}</td>
                  <td>
                    <button className="primary-btn" onClick={() => handleDelete(p.id)}>
                      Delete
                    </button>
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="5">No photos found.</td>
              </tr>
            )}
          </tbody>
        </table>

        <div className="form-grid">
          <input type="file" onChange={(e) => setFile(e.target.files[0])} />
          <input
            type="text"
            placeholder="Title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
          />
          <input
            type="text"
            placeholder="Description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
          />
          <button className="primary-btn" onClick={handleSave}>
            Save
          </button>
          <button className="secondary-btn" onClick={() => window.print()}>
            Print
          </button>
        </div>
      </section>

      {/* PUB UPLOAD */}
      <div className="upload-section">
