          onChange={(e) => setPublicaTitle(e.target.value)}
        />
        <input
          type="text"
          placeholder="Description"
          value={publicaDescription}
          onChange={(e) => setPublicaDescription(e.target.value)}
        />
        <button className="primary-btn" onClick={handleSavePublica}>
          Save Publica
        </button>
      </div>
    </section>
  );
}
