      useEffect(() => {
        const fetchPublications = async () => {
          const { data, error } = await supabase
            .from("publications")
            .select("id, title, description, photo_url, created_at")
            .order("created_at", { ascending: false });

          if (!error && data) {
            setPublications(data);
          }
        };

        fetchPublications();
      }, []);

