            .from("logo") //  using the "logo" table we created
            .select("logo_url")
            .order("created_at", { ascending: false })
            .limit(1);

          if (!error && data.length > 0) {
            setFederationLogo(data[0].logo_url);
          }
        };

        fetchLatestLogo();
      }, []);

      useEffect(() => {
        const fetchPhotos = async () => {
          const { data, error } = await supabase
            .from("pub")
            .select("photo_url")
            .order("created_at", { ascending: false });

          if (!error && data) {
            setPhotos(data);
          }
        };

        fetchPhotos();
      }, []);

