        const fetchPublicaData = async () => {
          const { data, error } = await supabase
            .from("publica")
            .select("title, description, photo_url")
            .order("created_at", { ascending: false });

          if (!error && data) {
            setPublica(data);
          }
        };

        fetchPublicaData();
      }, []);

      const nextSlide = () => {
        setCurrentIndex((prev) => (prev + 1) % publications.length);
      };

      const prevSlide = () => {
        setCurrentIndex((prev) =>
          (prev - 1 + publications.length) % publications.length
        );
      };

      useEffect(() => {
        let autoRotate;

        const startAutoRotate = () => {
          return setInterval(() => {
            setCurrentIndex((prev) => (prev + 1) % publications.length);
          }, 5000);
        };

        autoRotate = startAutoRotate();

        return () => clearInterval(autoRotate);
      }, [publications]);

      return (
        <div className="app-container">
          {/* HEADER */}
          <header className="bg-white text-gray-800 p-6 shadow-lg border-b-4 border-red-500">
            <div className="container mx-auto">
              <div className="federation-header">
                {federationLogo ? (
                  <img
                    src={federationLogo}
                    alt="Logo Fdration"
                    className="federation-logo"
                  />
                ) : (
                  <Shield className="w-16 h-16 text-green-700" />
                )}
                <h1 className="federation-title">
                  {federationName || "Algerian Judo Federation"}
                </h1>
              </div>
            </div>
          </header>

          {/* SHOWCASE HERO */}
          <section
            id="showcase"
            style={{
              backgroundImage:
                photos.length > 0 ? `url(${photos[0].photo_url})` : "none",
              backgroundSize: "cover",
              backgroundPosition: "center",
              color: "white",
              padding: "6rem 0",
              textAlign: "center",
              height: "300px",
            }}
          >
            <h1>Welcome to the Federation</h1>
            <p>Promoting Judo Excellence in Algeria</p>
          </section>
          {/*  CAROUSEL SECTION */}
          <section
            className="cube-carousel"
            onMouseEnter={() => clearInterval(autoRotate)}
            onMouseLeave={() => {
              autoRotate = startAutoRotate();
            }}
          >
            <div
              className="cube-slider"
              style={{
                transform: `rotateY(${currentIndex * -90}deg)`,
              }}
            >
              {publications.map((pub, index) => (
                <div key={pub.id} className="cube-face">
                  <img
                    src={pub.photo_url}
                    alt={pub.title}
                    className="cube-image"
                  />
                  <div className="cube-caption">
                    <h3>{pub.title}</h3>
                    <p>{pub.description}</p>
                  </div>
                </div>
              ))}
            </div>

            <div className="cube-controls">
              <button onClick={prevSlide}></button>
              <button onClick={nextSlide}></button>
            </div>
          </section>
          {/* MOVING IMAGE (Latest Publica) */}
          <div className="moving-image-container">
            {publica.length > 0 ? (
              (() => {
                const latest = publica[publica.length - 1]; // get the last item
                return (
                  <div className="moving-image-card">
                    <img src={latest.photo_url} alt={latest.title} />
                    <div className="moving-image-overlay">
                      <h3>{latest.title}</h3>
                      <p>{latest.description}</p>
                    </div>
                  </div>
                );
              })()
            ) : (
              <div className="moving-image-card">
                <img
                  src="https://images.pexels.com/photos/163403/judo-fight-sport-competition-163403.jpeg"
                  alt="Judo athletes"
                />
                <div className="moving-image-overlay">
                  <h3>Welcome to the Federation</h3>
                  <p>Promoting Judo Excellence in Algeria</p>
                </div>
              </div>
            )}
          </div>
          <BarBoxButton />

          {/* NAVIGATION */}
          <Navigation />

          {/* FOOTER */}
          <footer className="footer">
            <p>&copy; 2025 Algerian Judo Federation. All rights reserved.</p>
          </footer>
        </div>
      );
    }
  </boltAction>
